import React, {useState} from 'react';

const Calculator = () =>{

  const[option , setoption] = useState();
  const [grams , setgrams] = useState();
  const[min , setmin] = useState();


  const purity = (event) =>{
    setoption(event.target.value)
  }

  const calculate = () =>{  

        let min = 3000*grams;
      setmin(min)
  }



return(
  <>
 <div>GOLD LOAN CALCULATOR</div>
 <form>

  <label className="ml-24">Purity of Gold :</label>
   <select name ='option' onChange={purity}  >
    <option  value="OS" >OS</option>
    <option  value="22CT" >22CT</option>
    <option  value="KDM">KDM</option>
    <option  value="916">916</option>
  </select>

  <input type="number" placeholder="weight in grams" className="border-2 border-black ml-24"  name={grams} onChange={ (e) => setgrams(e.target.value) }/> 

  <button className="bg-red-500 border-2 border-black ml-24"  type="button" onClick={() => calculate()} >CALCULATE</button>
  <div className="color-red-900">{min}</div>

 </form>
  </>
)
}

export default Calculator; 


