          <input type="text" className="border-2 border-gray-600 my-2 " /><br />
          <input  type="number"  className="border-2 border-gray-600 " /><br />
        </div>
        </div>

      </form>
      <div className="w-full flex">
      <label className="w-3/6" >Customer ID :</label>  <input type="text" className="border-2 border-gray-600 w-3/6 " /> <br />
      <label className="w-3/6" > ID : </label> <input type="text" className="border-2 border-gray-600 w-3/6 " /><br />
      <label className="w-3/6" >Customer: </label> <input type="text" className="border-2 border-gray-600 w-3/6 " /><br />
      </div>
    </>
  );
};

export default Customer;
