import React, {useState} from 'react';
import Footer from "./Footer";

const Calculator = () =>{

  const[option , setoption] = useState("OS");
  const [grams , setgrams] = useState();
  const[min , setmin] = useState();
  const[max , setmax] = useState();



  const calculate = () =>{  

    if(option === "OS")
    {
      let min = 3000*grams;
      setmin(min);
      let max= 3600*grams;
      setmax(max);

    }
    else if (option === "22CT"){
      let min = 3000*grams;
      setmin(min);

      let max = 3900*grams;
      setmax(max)
    }
    else if (option === "KDM"){
      let min = 3000*grams;
      setmin(min);

      let max = 4000*grams;
      setmax(max)
    }
    else if (option === "916"){
      let min = 3000*grams;
      setmin(min);

      let max = 4200*grams;
      setmax(max)
    }
    else if (option === " "){
      let min = 0*grams;
     setmin(min);
     let max = 0*grams;
     setmax(max);
    }
  }



return(
  <>
 <div className="text-4xl lg:mt-24 mt-12 text-center">GOLD LOAN CALCULATOR</div>
 <form className="mt-12">
 <div className="lg:flex ">
      <div className="lg:w-2/6 lg:flex  mt-8">
   <label className="lg:ml-24 ml-12 lg:pr-0 pr-56">Name:</label>
   <input type="text" className="border-4 rounded lg:ml-0 lg:w-1/2 w-56 ml-12 hover:border-blue-900"/>
   </div>
   <div className="lg:w-2/6  mt-8">
   <label className="lg:ml-0 ml-12 lg:pr-0 pr-24">Contact Number:</label>
   <input type="lg:text" className="border-4 rounded lg:ml-0 ml-12 lg:w-1/2 w-56   hover:border-blue-900"/>
   </div>
   <div className="lg:w-2/6  mt-8  lg:pr-0 pr-24">
   <label className="lg:ml-0 ml-12">City:</label>
   <input type="text" className="border-4 rounded lg:ml-0 ml-12 lg:w-1/2 w-56  hover:border-blue-900"/>
   </div>
</div>

<br />


<div className="lg:mt-2  lg:flex ">
  <div className="lg:w-2/6 mt-8 lg:pr-0 pr-24">
 <label className="lg:ml-24 ml-12 "> Gold Type :</label>
   <select className="lg:w-1/2 w-56 lg:ml-0 ml-12"  >
    <option value="WITH STONE" >WITH STONE</option>
    <option  value="CHAIN">CHAIN</option>
    <option  value="SOLID">SOLID</option>
  </select> 
  </div>

<div  className="lg:w-2/6 mt-8 lg:pr-0 pr-24 ">
  <label className="lg:ml-0 ml-12 ">Purity of Gold :</label>
   <select className="lg:w-1/2 w-56  ml-12 lg:ml-0" name ='option'  onChange={ (event) => setoption(event.target.value) } >
    <option  value="OS" >OS</option>
    <option  value="22CT" >22CT</option>
    <option  value="KDM">KDM</option>
    <option  value="916">916</option>
  </select>
  </div>

  <div  className="lg:w-2/6 mt-8 lg:pr-0 pr-24">
<label className="lg:ml-0 ml-12" >Weight in.gms :
  <input type="number" placeholder="weight in grams" className="border-4 rounded  lg:w-1/2 w-56 lg:ml-0 ml-12 hover:border-blue-900"  value={grams} onChange={ (e) => setgrams(e.target.value) }/> 
  </label>
</div>
  </div>

<br />
  <button className="bg-yellow-300 rounded-full w-40  border-2   lg:mt-16 text-center  text-lg"  type="button" onClick={() => calculate()} >CALCULATE</button>
  <div className="color-red-900 lg:mt-24  mt-8  italic font-bold text-center text-2xl">Min Amount : ₹{min}</div>
  <div className="color-red-900  text-center italic font-bold text-2xl">Max Amount : ₹{max}</div>

 </form>

 <Footer />
  </>
)
}

export default Calculator; 


