import React, {useState} from 'react';
import Footer from "./Footer";

const Calculator = () =>{

  const[option , setoption] = useState("OS");
  const [grams , setgrams] = useState();
  const[min , setmin] = useState();
  const[max , setmax] = useState();



  const calculate = () =>{  

    if(option === "OS")
    {
      let min = 3000*grams;
      setmin(min);
      let max= 3500*grams;
      setmax(max);

    }
    else if (option === "22CT"){
      let min = 3000*grams;
      setmin(min);

      let max = 3600*grams;
      setmax(max)
    }
    else if (option === "KDM"){
      let min = 3000*grams;
      setmin(min);

      let max = 3600*grams;
      setmax(max)
    }
    else if (option === "916"){
      let min = 3000*grams;
      setmin(min);

      let max = 3750*grams;
      setmax(max)
    }
  }



return(
  <>
 <div className="text-4xl mt-24 ml-24">GOLD LOAN CALCULATOR</div>
 <form className="mt-12">
   <label className="ml-24">Name:</label>
   <input type="text" className="border-2 border-black"/>
   <label className="ml-24">Contact Number:</label>
   <input type="text" className="border-2 border-black"/>
   <label className="ml-24">Email Address:</label>
   <input type="text" className="border-2 border-black"/> 
<br />


<div className="mt-24">
 <label className="ml-24"> Gold Type :</label>
   <select  >
    <option value="WITH STONE" >WITH STONE</option>
    <option  value="CHAIN">CHAIN</option>
    <option  value="SOLID">SOLID</option>
  </select> 


  <label className="ml-52 ">Purity of Gold :</label>
   <select name ='option'  onChange={ (event) => setoption(event.target.value) } >
    <option  value="OS" >OS</option>
    <option  value="22CT" >22CT</option>
    <option  value="KDM">KDM</option>
    <option  value="916">916</option>
  </select>

<label className="ml-72 " >Weight In Grams :
  <input type="number" placeholder="weight in grams" className="border-2 border-black "  value={grams} onChange={ (e) => setgrams(e.target.value) }/> 
  </label>
  </div>

<br />
  <button className="bg-red-500 border-2 border-black ml-24 mt-24"  type="button" onClick={() => calculate()} >CALCULATE</button>
  <div className="color-red-900 mt-24 ml-24">Min Amount :{min}</div>
  <div className="color-red-900 ml-24">Max Amount :{max}</div>

  <div className="flex">
    <div className="w-2/6">
    <label className="ml-24">Name:</label>
   <input type="text" className="border-2 border-black"/>
    </div>
    <div className="w-2/6">
    <label className="ml-24">Name:</label>
   <input type="text" className="border-2 border-black"/>
    </div>
    <div className="w-2/6">
    <label className="ml-24">Name:</label>
   <input type="text" className="border-2 border-black"/>
    </div>
  </div>
  <div className="flex">
    <div className="w-2/6">
    <label className="ml-24">Name:</label>
   <input type="text" className="border-2 border-black"/>
    </div>
    <div className="w-2/6">
    <label className="ml-24">Name:</label>
   <input type="text" className="border-2 border-black"/>
    </div>
    <div className="w-2/6">
    <label className="ml-24">Name:</label>
   <input type="text" className="border-2 border-black"/>
    </div>
  </div>
 </form>

 <Footer />
  </>
)
}

export default Calculator; 


