import React, {useState} from 'react';

const Calculator = () =>{

  const[prinicipal , setprinicipal] = useState();


return(
  <>
 <div>GOLD LOAN CALCULATOR</div>
 <form>



  <label className="ml-24"> Gold Type</label>
   <select  >
    <option value="WITH STONE" >WITH STONE</option>
    <option  value="CHAIN">CHAIN</option>
    <option  value="SOLID">SOLID</option>

  </select>

  <label className="ml-24">Purity of Gold</label>
   <select   >
    <option  value="OS" >OS</option>
    <option  value="22CT" >22CT</option>
    <option  value="KDM">KDM</option>
    <option  value="916">916</option>
  </select>

  <input type="number" placeholder="weight in grams" className="border-2 border-black ml-24" /> 

  <button className="bg-red-500 border-2 border-black ml-24"  type="button" >CALCULATE</button>

 </form>
  </>
)
}

export default Calculator; 

