import React from "react";
import Banner from "./Banner";
import About from './About';
import Ourservices from "./Ourservices";
import Branches from "./Branches";
//  import Contact from "./Contact";
import Footer from "./Footer";
import Companies from "./Ourgroupofcompanies";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import {IntlProvider, FormattedMessage} from 'react-intl'

const messagesInFrench = {
  name: "Our Story",

  nameOne:"Karur Gold Finance, with a reputation that has been shaped over decades with high quality practices, total customer satisfaction and steady growth, spanning decades in the field of business, is a legacy built on God-given values of trust, truth, transparency and tradition and has become one of the top business houses today by the grace of God.we have three branches in tamilnadu with over 50 employees. we have walk in customer of over 2000 happy customers.",
}

const Home = () =>{
  const settings = {
    dots: true,
    infinite: true,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000,


};
return(
  <>
  <IntlProvider messages={messagesInFrench} >

     <div className="slider m-0 p-0">
      <Slider {...settings}>
          <div>
            <img  className="w-full max-h-screen" src="/Image/banner1.jpg"  alt="name"/>
          </div>
          <div>
            <img  className="w-full max-h-screen" src="/Image/banner2.jpg"  alt="name"/>
          </div>
          <div>
            <img  className="w-full max-h-screen" src="/Image/banner3.jpg"  alt="name"/>
          </div>
          <div>
            <img  className="w-full max-h-screen" src="/Image/banner4.jpg"  alt="name"/>
          </div>
        </Slider>
    </div>


    <div className="lg:flex mt-24 lg:ml-24 mx-8">
           <div className=" lg:w-2/4  ">
             <img alt="..." className="align-middle border-none max-w-full h-auto rounded-full" src="/Image/santhosh.JPG" />
             <p className="text-center text-md">Santhose Murugan</p>
             <p className="text-center  text-md">Founder,KGF</p>
         </div>
         <div className="lg:w-2/4  lg:mr-24  text-justify ">
           <b className="lg:text-2xl text-xl lg:pl-12 focus:bg-red-800"> <FormattedMessage id="name"/> </b>
           <p className="lg:text-xl text-sm lg:pl-12"><FormattedMessage id="nameOne"/> </p> 
          </div>

       </div>
    </IntlProvider>
  </>

)
}

export default Home;
