import React, {useState} from 'react';

const Calculator = () =>{

  const[option , setoption] = useState("OS");
  const [grams , setgrams] = useState();
  const[min , setmin] = useState();
  const[max , setmax] = useState();



  const calculate = () =>{  

    if(option === "OS")
    {
      let min = 3000*grams;
      setmin(min);
      let max= 3500*grams;
      setmax(max);

    }
    else if (option === "22CT"){
      let min = 3000*grams;
      setmin(min);

      let max = 3600*grams;
      setmax(max)
    }
    else if (option === "KDM"){
      let min = 3000*grams;
      setmin(min);

      let max = 3600*grams;
      setmax(max)
    }
    else if (option === "916"){
      let min = 3000*grams;
      setmin(min);

      let max = 3750*grams;
      setmax(max)
    }
  }



return(
  <>
 <div className="text-6xl">GOLD LOAN CALCULATOR</div>
 <form>
  <label className="ml-24">Purity of Gold :
   <select name ='option'  onChange={ (event) => setoption(event.target.value) } >
    <option  value="OS" >OS</option>
    <option  value="22CT" >22CT</option>
    <option  value="KDM">KDM</option>
    <option  value="916">916</option>
  </select>
  </label>
  <input type="number" placeholder="weight in grams" className="border-2 border-black ml-24"  value={grams} onChange={ (e) => setgrams(e.target.value) }/> 

  <button className="bg-red-500 border-2 border-black ml-24"  type="button" onClick={() => calculate()} >CALCULATE</button>
  <div className="color-red-900">Min Amount :{min}</div>
  <div className="color-red-900">Max Amount :{max}</div>

 </form>
  </>
)
}

export default Calculator; 


